package leetcode.no744_FindSmallestLetterGreaterThanTarget;

import static org.junit.Assert.*;

import org.junit.After;
import org.junit.Before;
import org.junit.Test;

public class SolutionTest {

	private Solution solution;

	@Before
	public void setUp() throws Exception {
		solution = new Solution();
	}

	@After
	public void tearDown() throws Exception {
		solution = null;
	}

	@Test
	public void initiated() {
		assertNotNull(solution);
	}

	private void assertLetter(char expected, char[] letters, char target) {
		char actual = solution.nextGreatestLetter(letters, target);
		assertEquals(expected, actual);
	}

	@Test
	public void lesserThan1st() {
		char[] letters = { 'c', 'f', 'j' };
		char target = 'a';
		char expected = 'c';
		assertLetter(expected, letters, target);
	}

	@Test
	public void equalsTo1st() {
		char[] letters = { 'c', 'f', 'j' };
		char target = 'c';
		char expected = 'f';
		assertLetter(expected, letters, target);
	}

	@Test
	public void between1stAnd2nd() {
		char[] letters = { 'c', 'f', 'j' };
		char target = 'e';
		char expected = 'f';
		assertLetter(expected, letters, target);
	}

	@Test
	public void equalsTo2nd() {
		char[] letters = { 'c', 'f', 'j' };
		char target = 'f';
		char expected = 'j';
		assertLetter(expected, letters, target);
	}

	@Test
	public void between2ndAnd3rd() {
		char[] letters = { 'c', 'f', 'j' };
		char target = 'h';
		char expected = 'j';
		assertLetter(expected, letters, target);
	}

	@Test
	public void equalsTo3rd() {
		char[] letters = { 'c', 'f', 'j' };
		char target = 'j';
		char expected = 'c';
		assertLetter(expected, letters, target);
	}

	@Test
	public void graterThan3rd() {
		char[] letters = { 'c', 'f', 'j' };
		char target = 'k';
		char expected = 'c';
		assertLetter(expected, letters, target);
	}
	
	@Test
	public void longLetters() {
		char[] letters = "".toCharArray();
		char target = 'z';
		char expected = 'a';
		assertLetter(expected, letters, target);
	}

}
